import React, { useState } from "react";
import './home.css'

import { Link } from 'react-router-dom';


const Home = () => {
    const [input, setInput] = useState({})

    const handleInput = (e) => {
        setInput({
            ...input,
            [e.target.name]: e.target.value
        })
    }
    var miStorage = window.localStorage;
    const login = () => {
        var {email, pass} = input
        return axios.post(`http://localhost:3005/login`, {email, pass})
        .then()
    }
    return (
        <div className='centered' >
            <div className='inputs'>
                <p>E-mail</p>
                <input className='input' value={input.email} name='email' onChange={(e) => handleInput(e)} />

                <p>Password</p>
                <input className='input' value={input.pass} name='pass' onChange={(e) => handleInput(e)} />
                <button
                    onClick={() => login()}
                    style={{ marginTop: '1vw', width: '10vw', height: '3vw', backgroundColor: '#3045DB', outline: 'none', border: '#2EC2D1 1px solid' }}>
                    LOGIN
                </button>
                <div className='registerButton'>
                    <span>Don't have an account?</span><br />
                    <Link to='register'>Register now!</Link>
                </div>
            </div>
        </div>
    )
}

export default Home;
